mapper.select:
SELECT * 
FROM MEMBER 
WHERE M_ID = #{param1} AND M_PW = #{param2}

mapper.update:
UPDATE MEMBER SET
M_PW = #{param1}, M_NAME =
#{param2}, M_ADDRESS = #{param3}
WHERE M_ID = #{param4}

mapper.select:
SELECT COUNT(*) FROM MEMBER
WHERE M_ID = #{param1}
AND M_EMAIL = #{param2}

mapper.update:
UPDATE MEMBER
SET M_PW = #{param2}
WHERE
M_ID = #{param1}

UPDATE MEMBER
SET M_MONEY = M_MONEY + #{param2}
WHERE M_NO = (SELECT S_NO FROM PRODUCT WHERE P_NO = #{param1})

UPDATE MEMBER
SET M_MONEY = M_MONEY - #{param1}
WHERE M_NO = 1

mapper.select:
SELECT M_ID 
FROM MEMBER 
WHERE M_NO = #{param1}

mapper.update:
UPDATE MEMBER 
SET M_POINT = M_POINT + #{param1}
WHERE M_NO = (SELECT B_NO FROM PRODUCT WHERE P_NO = #{param2})

UPDATE MEMBER 
SET M_MONEY = M_MONEY - #{param1}
WHERE M_NO = 1

UPDATE MEMBER 
SET M_MONEY = M_MONEY + #{param2}
WHERE M_NO = #{param1}

UPDATE MEMBER 
SET M_STATE = 0 
WHERE M_NO = #{param1}
