mapper.select:
SELECT *
FROM PRODUCT 
WHERE P_NO = #{p_No}

mapper.update:
UPDATE PRODUCT
SET P_STATE = 2, B_NO = #{param1}
WHERE P_NO = #{param2}

UPDATE PRODUCT
SET P_STATE = -1 
WHERE P_NO = #{param1}

UPDATE PRODUCT
SET P_NAME = #{param2}, P_CARTEGORY = #{param3}, P_CONTENT = #{param4}, P_PRICE = #{param5}, P_IMG_PATH = #{param6}
WHERE P_NO = #{param1}

mapper.select:
SELECT COUNT(*) 
FROM PRODUCT 
WHERE B_NO = #{M_NO}

SELECT COUNT(*)
FROM PRODUCT
WHERE S_NO = #{M_NO}

mapper.update:
UPDATE PRODUCT
SET P_HIT = P_HIT + 1
WHERE P_NO = #{param1}

UPDATE PRODUCT
SET B_NO = NULL, P_STATE = 1
WHERE P_NO = #{param1}
